import { FaGithub, FaLinkedin, FaDocker, FaReact } from "react-icons/fa";
import {
	DiVisualstudio,
	DiGithubBadge,
	DiSass,
	DiStackoverflow,
	DiWordpress
} from "react-icons/di";
import { useState } from "react";
import styled from "styled-components";
function HomePage() {
	const [accentColor, setAccentColor] = useState("#fff");
	const [descriptionText, setDescriptionText] = useState("");
	return (
		<>
			<Header>
				<h1>Sean Lyons</h1>
				<ul>
					<li>
						<FaGithub />
					</li>
					<li>
						<FaLinkedin />
					</li>
				</ul>
			</Header>
			<main>
				<section className="technologies">
					<div className="icons">
						<div
							className="icon"
							id="vscode"
							onFocus={() => setAccentColor("#005ba4")}
							tabIndex="3"
						>
							<DiVisualstudio />
						</div>
						<div
							className="icon"
							id="docker"
							onFocus={() => setAccentColor("#389fee")}
							tabIndex="4"
						>
							<FaDocker />
						</div>
						<div
							className="icon"
							id="react"
							onFocus={() => setAccentColor("#61dafb")}
							tabIndex="5"
						>
							<FaReact />
						</div>
						<div
							className="icon"
							id="github"
							onFocus={() => setAccentColor("#2ec04f")}
							tabIndex="6"
						>
							<DiGithubBadge />
						</div>
						<div
							className="icon"
							id="sass"
							onFocus={() => setAccentColor("#cf649a")}
							tabIndex="7"
						>
							<DiSass />
						</div>
						<div
							className="icon"
							id="stackoverflow"
							onFocus={() => setAccentColor("#da670b")}
							tabIndex="8"
						>
							<DiStackoverflow />
						</div>
						<div
							className="icon"
							id="wordpress"
							onFocus={() => setAccentColor("#005ba4")}
							tabIndex="9"
						>
							<DiWordpress />
						</div>
					</div>
					<div className="description">
						<div className="description-text">
							<p>{descriptionText}</p>
						</div>
					</div>
				</section>
				<section className="skillsexperience">
					<p>on the way</p>
				</section>
			</main>
			<footer></footer>
			<style jsx global>{`
				@import url("https://fonts.googleapis.com/css?family=Abril+Fatface&display=swap");
				html,
				body {
					margin: 0;
					padding: 0;
				}
				h1 {
					font-family: "Abril Fatface", sans-serif;
				}
				.icons {
					display: flex;
					width: 100%;
					height: 30px;
					overflow: visible;
				}
				.icon {
					display: flex;
					flex: 1 0 auto;
					justify-content: center;
					border-bottom: 2px solid #333;
					border-left: 1px solid #333;
					border-right: 1px solid #333;
					background: #61ce70;
					color: #fff;
					padding: 25px 0;
					font-size: 24px;
					height: 30px;
					transition: 0.3s all;
				}
				.icon:focus {
					height: 50px;
					outline: none;
					border: 1px solid rgba(0, 0, 0, 0);
					background: ${accentColor};
				}
				.description {
					min-height: 150px;
					background: ${accentColor};
					transition: 0.3s all;
				}
				.description-text {
					background: #fff;
					color: #333;
				}
				main {
					z-index: 1;
				}
			`}</style>
		</>
	);
}
const Header = styled.header`
	background: #61ce70;
	padding: 50px 0;
	color: #fff;
	text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
	border-bottom: 8px solid #333;
	box-shadow: 0px 2px 12px 2px rgba(0, 0, 0, 0.6);
	position: relative;
	z-index: 2;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;

	ul {
		list-style: none;
		display: flex;
		padding-left: 0;
		li {
			padding: 25px;
			font-size: 48px;
			:global(svg) {
				color: red;
			}
		}
	}
`;

export default HomePage;
