const MultiForm = formType => {
	return (
		<div className="multi-form">
			<div className="tlwrapper">
				<ol className="steps">
					<li className=" step active-step">step one</li>
					<li className="step">step two</li>
					<li className="step">step three</li>
					<li className="step">step four</li>
				</ol>
			</div>
			<form>
				<div className="slide active-slide" id="slide1">
					<p>How many repples is the bag of bobbish worth?</p>
					<input type="text" placeholder="repples???"></input>
					<button className="btn"></button>
				</div>
				<div className="slide" id="slide2">
					<p>How many repples is the plumbus worth?</p>
					<input type="text" placeholder="repples???"></input>{" "}
				</div>
				<div className="slide" id="slide3">
					<p>How many repples are the crushed red party cups worth?</p>
					<input type="text" placeholder="repples???"></input>{" "}
				</div>
			</form>
			<style jsx>{`
				.multi-form {
					height: 100%;
					width: 100%;
					display: block;
				}
				.slide {
					opacity: 0;
				}
				.active-slide {
					opacity: 1;
				}
				ol {
					display: flex;
				}
				li {
					padding: 5px;
					width: 25%;
				}
				ol li::before {
					content: counter(my-awesome-counter) ". ";
					color: red;
					font-weight: bold;
				}

				.steps {
					clear: both;
					list-style: none;
					padding-left: 2%;
				}
				.steps li {
					margin: 2em 0;
					padding-top: 1em;
					display: block;
					position: relative;
					counter-increment: inst;
				}
				.steps li::before {
					content: counter(inst);

					background: #0671c1;
					color: #fff;

					font-size: 1em;
					text-shadow: 1px 1px rgba(255, 150, 0, 0.5);

					font-size: 1.5em;
					text-align: center;

					padding-top: 0;
					padding-left: 2.25%;
					left: -5%;
					top: -0.65em;
					height: 1.35em;
					width: 1.35em;
					position: absolute;

					transition: all 0.2s ease-in-out;

					z-index: -1;
				}
			`}</style>
		</div>
	);
};

export default MultiForm;
