const MultiForm = formType => {
	return (
		<div className="multi-form">
			<div className="tlwrapper">
				<ol>
					<li className=" step active-step">step one</li>
					<li className="step">step two</li>
					<li className="step">step three</li>
					<li className="step">step four</li>
				</ol>
			</div>
			<form>
				<div className="slide active-slide" id="slide1">
					<p>How many repples is the bag of bobbish worth?</p>
					<input type="text" placeholder="repples???"></input>
					<button className="btn"></button>
				</div>
				<div className="slide" id="slide2">
					<p>How many repples is the plumbus worth?</p>
					<input type="text" placeholder="repples???"></input>{" "}
				</div>
				<div className="slide" id="slide3">
					<p>How many repples are the crushed red party cups worth?</p>
					<input type="text" placeholder="repples???"></input>{" "}
				</div>
			</form>
			<style jsx>{`
				.multi-form {
					height: 100%;
					width: 100%;
					display: block;
				}
				.slide {
					opacity: 0;
				}
				.active-slide {
					opacity: 1;
				}
				ol {
					display: flex;
				}
				li {
					padding: 5px;
					width: 25%;
				}
				ol li::before {
					content: counter(my-awesome-counter) ". ";
					color: red;
					font-weight: bold;
				}
			`}</style>
		</div>
	);
};

export default MultiForm;
