import { FaGithub, FaLinkedin, FaDocker, FaReact } from "react-icons/fa";
import {
	DiVisualstudio,
	DiGithubBadge,
	DiSass,
	DiStackoverflow,
	DiWordpress
} from "react-icons/di";
import { useState } from "react";
import styled from "styled-components";
function HomePage() {
	const [accentColor, setAccentColor] = useState("#fff");
	const [descriptionText, setDescriptionText] = useState("");
	const setTechs = (color, text) => {
		setAccentColor(color);

		setDescriptionText(text);
	};
	return (
		<>
			<Header>
				<h1>Sean Lyons</h1>
				<ul>
					<li>
						<FaGithub />
					</li>
					<li>
						<FaLinkedin />
					</li>
				</ul>
			</Header>
			<main>
				<section className="technologies">
					<div className="icons">
						<div
							className="icon"
							id="vscode"
							onFocus={() =>
								setTechs(
									"#272935",
									"Curabitur semper congue nisi volutpat vestibulum. Etiam vitae dui dui. Etiam condimentum pellentesque quam id dapibus. Suspendisse pulvinar porta dapibus. Nam hendrerit mi ac volutpat dignissim. Suspendisse potenti. Sed tempus scelerisque tortor, vitae tincidunt nulla. Quisque sagittis erat urna, vel volutpat est iaculis vehicula. Vestibulum ante ipsum primis in faucibus orci luctus et ultrices posuere cubilia Curae; Mauris molestie tellus lectus, eu vulputate orci dapibus nec. Phasellus tempor ipsum non risus fermentum, et rutrum magna mattis. Donec eleifend porta eros. Integer vel justo dignissim elit tempor blandit. Nunc ut tortor in erat elementum pretium. Duis ut ex vel erat venenatis fermentum elementum sit amet massa. Etiam semper urna vestibulum, pretium tortor vestibulum, dictum dui."
								)
							}
							tabIndex="3"
						>
							<DiVisualstudio />
						</div>
						<div
							className="icon"
							id="docker"
							onFocus={() => setTechs("#032144", "testtesttest")}
							tabIndex="4"
						>
							<FaDocker />
						</div>
						<div
							className="icon"
							id="react"
							onFocus={() => setTechs("#29B1EF", "testtesttest")}
							tabIndex="5"
						>
							<FaReact />
						</div>
						<div
							className="icon"
							id="github"
							onFocus={() => setTechs("#2DBD4E", "testtesttest")}
							tabIndex="6"
						>
							<DiGithubBadge />
						</div>
						<div
							className="icon"
							id="sass"
							onFocus={() => setTechs("#CF649A", "testtesttest")}
							tabIndex="7"
						>
							<DiSass />
						</div>
						<div
							className="icon"
							id="stackoverflow"
							onFocus={() => setTechs("#F48025", "testtesttest")}
							tabIndex="8"
						>
							<DiStackoverflow />
						</div>
						<div
							className="icon"
							id="wordpress"
							onFocus={() => setTechs("#D7DADE", "testtesttest")}
							tabIndex="9"
						>
							<DiWordpress />
						</div>
					</div>
					<div className="description">
						<div className="description-text">
							<p>{descriptionText}</p>
						</div>
					</div>
				</section>
				<section className="skillsexperience">
					<p>on the way</p>
				</section>
			</main>
			<footer></footer>
			<style jsx global>{`
				@import url("https://fonts.googleapis.com/css?family=Ubuntu|Uncial+Antiqua&display=swap");
				html,
				body {
					margin: 0;
					padding: 0;
					background: #fffff0;
				}
				h1,
				h2,
				h3,
				h4,
				h5,
				h6 {
					font-family: "Uncial Antiqua", sans-serif;
				}
				p {
					font-family: "Ubuntu", sans-serif;
				}
				.icons {
					display: flex;
					width: 100%;
					overflow: visible;
					background: ${accentColor};
					height: 75px;
				}
				.icon {
					display: flex;
					flex: 1 0 auto;
					justify-content: center;
					border-bottom: 2px solid #333;
					border-left: 1px solid #333;
					border-right: 1px solid #333;
					background: #61ce70;
					color: #fff;
					padding: 25px 0;
					font-size: 24px;
					height: 30px;
					transition: 0.3s all;
				}
				.icon:focus {
					height: 50px;
					outline: none;
					border: 1px solid rgba(0, 0, 0, 0);
					background: ${accentColor};
				}
				.description {
					min-height: 150px;
					background: ${accentColor};
					transition: 0.3s all;
					display: flex;
					flex-direction: column;
					justify-content: center;
					align-items: center;
				}
				.description-text {
					background: #fff;
					color: #333;
					width: 80%;
					height: auto;
					padding: 15px 10px;
				}
				main {
					z-index: 1;
				}
			`}</style>
		</>
	);
}
const Header = styled.header`
	background: #61ce70;
	padding: 50px 0;
	color: #fff;
	text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
	border-bottom: 8px solid #333;
	box-shadow: 0px 2px 12px 2px rgba(0, 0, 0, 0.6);
	position: relative;
	z-index: 2;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;

	ul {
		list-style: none;
		display: flex;
		padding-left: 0;
		li {
			padding: 25px;
			font-size: 48px;
			:global(svg) {
				color: red;
			}
		}
	}
`;

export default HomePage;
